import React from 'react';
import {MyName, CrtTech} from './App';
const ComC = () =>{
    return(
         <>
            <MyName.Consumer>
                {
                    (fName) => {
                      return (
                      <CrtTech.Consumer>{
                        (ctech) =>{
                            return(
                                <div style={{textAlign:'center'}}> <h1>Hi <span style={{color:'red', fontSize:"33px"}} >{fName}</span></h1>
                                <h2>Above content call by create <span style={{color:'red', fontSize:"33px"}} >{ctech}</span> </h2>
                                </div>
                            )
                      }}</CrtTech.Consumer>
                      )
                    }
                }
              
            </MyName.Consumer>
           
         </>
    );
};

export default ComC;
