import React from 'react';
import {MyName} from './App';
const ComC = () =>{
    return(
         <>
            <MyName.Consumer>
                {
                    (fName) => {
                      return  <h1>Hi {fName}</h1>
                    }
                }
              
            </MyName.Consumer>
            <h2>Above content call by createContext </h2>
         </>
    );
};

export default ComC;
