import React from 'react';
import {MyName} from './App';
const ComC = () =>{
    return(
         <>
            <MyName.Consumer>
                {
                    (fName) => {
                      return <> <h1>Hi {fName}</h1>
                      <h2>Above content call by create Context </h2>
                      </>
                    }
                }
              
            </MyName.Consumer>
           
         </>
    );
};

export default ComC;
