import React, { useEffect, useState } from 'react';
const CrtCard = () =>{

  const [users, setUsers] = useState([]);

  // const getUsers = async () => {
  //   const response = await fetch('https://api.github.com/users');
  //   setUsers(await response.json()); 
  // }

  const getUsers =  () => {

    fetch('./data.json')
      .then((response) => {
        return response.json();
      })
      .then((myJson) => {
        console.log(myJson);
        setUsers(myJson)
      }).catch((err) => {
        console.log(err, ' error')
      })
  }

  useEffect(() => {
    getUsers(); 
  }, []);

  return(
    <>
    <h1>heading</h1>
    <section>
      {
      users.map((crtElem)=>{
        return(
          <div className='cardWrap' key={crtElem.id}>
          <div className="card">
            <h2>{crtElem.firstName}</h2>
            <p>Text</p>
          </div>
        </div>
        )
      })
    }
    </section>
    </>

  )
}


export default CrtCard;
